import React, { useCallback, useRef, useState } from "react";
import classNames from "classnames";
import style from "../styles/book.module.scss";
import getInTouchStyle from "../styles/getInTouchLeft.module.scss";
import DoublePage from "./DoublePage";
import Cover from "./Cover";
import GetBookPage from "./helper/GetBookPage";
import HTMLFlipBook from "react-pageflip";
import LeftPage from "./LeftPage";
import RightPage from "./RightPage";
import Image from "next/image";
import Bookmarks from "./Bookmarks";
import Modal from "./helper/Modal";
import { AiOutlinePlus } from "react-icons/ai";
import Form from "./helper/Form";
import {
  Accordion,
  AccordionActions,
  AccordionDetails,
  AccordionSummary,
  Typography,
} from "@mui/material";
import { MdOutlineExpandMore } from "react-icons/md";

const Book = ({
  pages,
  bookmarks,
  currentPage,
  zIndexPage,
  changePage,
  changeCustomPage,
  bookRef,
}) => {
  const [qaIndex, setQaIndex] = useState(0);
  const pageFlipRef = useRef(null);
  const [activePage, setActivePage] = useState(null);
  const [faqModal, setFaqModal] = useState(false);
  const [getInTouchModal, setGetInTouchModal] = useState(false);
  const [videoModal, setVideoModal] = useState(false);

  const onFlip = useCallback((e) => {
    setActivePage(e.data);
  }, []);

  return (
    <>
      <div
        style={{
          width: "calc(132 * var(--vh))",
          height: "calc(220 * var(--vh))",
          display: "flex",
          justifyContent: "center",
          alignItems: "center",
        }}
      >
        <HTMLFlipBook
          width={490}
          height={733}
          size={"stretch"}
          maxShadowOpacity={0.5}
          showCover={true}
          mobileScrollSupport={true}
          ref={pageFlipRef}
          onFlip={onFlip}
          style={{
            transform:
              Math.ceil(
                pageFlipRef.current?.pageFlip()?.getCurrentPageIndex() / 2
              ) === 0 || pageFlipRef.current === null
                ? "translateX(-25%)"
                : "translateX(0%)",
            transition: "transform .75s ease-in-out",
          }}
        >
          <Image
            src={"/blackBookCover.webp"}
            alt="book cover"
            placeholder="blur"
            blurDataURL={
              "data:image/jpeg;base64,"
            }
            loader={() => "/blackBookCover.webp"}
            fill
            unoptimized={true}
            style={{ objectFit: "cover" }}
          />

          {pages?.map((page, index) => {
            return [
              <div key={`${page.sys.contentType.sys.id}-left`}>
                <LeftPage
                  backgroundImg={`url('${page.fields.backgroundImage[0].fields.file.url}')`}
                  content={
                    <GetBookPage
                      id={page.sys.contentType.sys.id}
                      page={page}
                      side="left"
                      changeCustomPage={changeCustomPage}
                      pages={pages}
                      qaIndex={qaIndex}
                      setQaIndex={setQaIndex}
                      pageFlip={pageFlipRef}
                      setFaqModal={setFaqModal}
                      setGetInTouchModal={setGetInTouchModal}
                      setVideoModal={setVideoModal}
                    />
                  }
                  thisPageIndex={index}
                />
              </div>,
              <div key={`${page.sys.contentType.sys.id}-right`}>
                <RightPage
                  backgroundImg={`url('${page.fields.backgroundImage[1].fields.file.url}')`}
                  content={
                    <GetBookPage
                      id={page.sys.contentType.sys.id}
                      page={page}
                      side="right"
                      changeCustomPage={changeCustomPage}
                      pages={pages}
                      qaIndex={qaIndex}
                      setQaIndex={setQaIndex}
                      pageFlip={pageFlipRef}
                      setFaqModal={setFaqModal}
                      setGetInTouchModal={setGetInTouchModal}
                      setVideoModal={setVideoModal}
                    />
                  }
                  thisPageIndex={index}
                />
              </div>,
            ];
          })}
        </HTMLFlipBook>
        <Bookmarks
          pageFlipRef={pageFlipRef}
          bookmarks={bookmarks}
          pages={pages}
        />
      </div>

      {faqModal && (
        <Modal setShowModal={setFaqModal}>
          <div className={classNames(style.questions)}>
            {faqModal?.map(
              ({ fields: { question, answer }, sys: { id } }, index) => (
                <Accordion
                  disableGutters
                  sx={{ backgroundColor: "#e1e1e1" }}
                  key={id}
                >
                  <AccordionSummary
                    expandIcon={
                      <span style={{ fontSize: "calc(4 * var(--vh))" }}>
                        <MdOutlineExpandMore />
                      </span>
                    }
                    aria-controls="panel1a-content"
                    id="panel1a-header"
                  >
                    <Typography>{question}</Typography>
                  </AccordionSummary>
                  <AccordionDetails>
                    <Typography>{answer}</Typography>
                  </AccordionDetails>
                </Accordion>
              )
            )}
          </div>
        </Modal>
      )}

      {getInTouchModal && (
        <Modal setShowModal={setGetInTouchModal}>
          <Form style={getInTouchStyle} />
        </Modal>
      )}

      {videoModal && (
        <Modal setShowModal={setVideoModal} white>
          {videoModal}
        </Modal>
      )}
    </>
  );
};

export default Book;
